BEGIN TRANSACTION;
CREATE TABLE "verlag" ("id" integer primary key autoincrement,"created_at" datetime,"updated_at" datetime,"deleted_at" datetime,"name" varchar(255),"strasse" varchar(255),"plz" varchar(255),"ort" varchar(255) );
CREATE TABLE "person" ("id" integer primary key autoincrement,"created_at" datetime,"updated_at" datetime,"deleted_at" datetime,"name" varchar(255),"vorname" varchar(255),"strasse" varchar(255),"plz" varchar(255),"ort" varchar(255),"geburtstag" datetime,"mitglied_seit" datetime,"gruppe" integer,"email" varchar(255),"rolle" integer );
CREATE TABLE "lied" ("id" integer primary key autoincrement,"created_at" datetime,"updated_at" datetime,"deleted_at" datetime,"titel" varchar(255),"untertitel" varchar(255),"jahr" integer,"komponist_id" integer,"texter_id" integer,"arrangeur_id" integer,"verlag_id" integer );
CREATE TABLE "gruppe" ("id" integer primary key autoincrement,"created_at" datetime,"updated_at" datetime,"deleted_at" datetime,"name" varchar(255) );
CREATE INDEX idx_verlag_deleted_at ON "verlag"(deleted_at) ;
CREATE INDEX idx_person_deleted_at ON "person"(deleted_at) ;
CREATE INDEX idx_lied_deleted_at ON "lied"(deleted_at) ;
CREATE INDEX idx_gruppe_deleted_at ON "gruppe"(deleted_at) ;
COMMIT;
